import React, { useState } from "react";
import noxData from "../data/nox.json";
import LineChart from "./LineChart";

const categories = noxData.map((row: any) => row["Emission source categories"]);

const Dashboard: React.FC = () => {
  const [selected, setSelected] = useState<string[]>(categories);

  const toggleCategory = (cat: string) => {
    setSelected((prev) =>
      prev.includes(cat) ? prev.filter((c) => c !== cat) : [...prev, cat]
    );
  };

  return (
    <div>
      <div style={{ marginBottom: 20 }}>
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => toggleCategory(cat)}
            style={{
              margin: "0 5px",
              background: selected.includes(cat) ? "#007bff" : "#ccc",
              color: "#fff",
              border: "none",
              padding: "5px 10px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            {cat}
          </button>
        ))}
      </div>
      <LineChart data={noxData} selectedCategories={selected} />
    </div>
  );
};

export default Dashboard;
