import React from "react";
import { Line } from "react-chartjs-2";
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

interface Props {
  data: any[];
  selectedCategories: string[];
}

const LineChart: React.FC<Props> = ({ data, selectedCategories }) => {
  if (!data || data.length === 0) return <div>No data available.</div>;

  const years = Object.keys(data[0]).filter(
    (key) => /^\d{4}-\d{2}-\d{2}/.test(key)
  );

  const datasets = data
    .filter((row) => selectedCategories.includes(row["Emission source categories"]))
    .map((row) => ({
      label: row["Emission source categories"],
      data: years.map((year) => Number(row[year])),
      fill: false,
      borderColor: "#" + Math.floor(Math.random() * 16777215).toString(16),
    }));

  console.log("Selected categories:", selectedCategories);
  console.log("Years:", years);
  console.log("Datasets:", datasets);

  return (
    <Line
      data={{
        labels: years,
        datasets,
      }}
      options={{
        responsive: true,
        plugins: {
          legend: { display: false },
        },
      }}
    />
  );
};

export default LineChart;
